mapper.select:
select * from question where qu_where like concat('%',#{qu_where}, '%')
and qu_name like concat('%',#{cri.search},'%')
order by qu_ori_num desc, qu_num asc limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
update question set
	qu_title = #{qu_title},
	qu_content = #{qu_content}
where qu_num = #{qu_num}
